select id, userId, customerName, (select group_concat('跟进信息:',content ,'[By:',userName,'At:',createDate,']\n</br>', '' ORDER BY id DESC) from cufollow where id in (SELECT id FROM cufollow WHERE cufollow.customerId=cudata.id)) content, telephone, gender, apartment , totalPrice, purpose, cusource, userName, (select name from ogdepartment where id=department) departmentName, trackDate, createDate from cudata where resources=2
and department=#{department}
AND customerName LIKE CONCAT(CONCAT('%',#{customerName}),'%')
and telephone=#{telephone}
and userId=#{userId}
and totalPrice between #{stotalPrice} and #{etotalPrice}
and purpose=#{purpose}
and apartment=#{apartment}
order by createDate desc,id asc

select id, userId, customerName, telephone, gender, apartment , totalPrice, purpose, cusource, userName, (select name from ogdepartment where id=department) departmentName, trackDate, createDate from cudata where resources=3
and department=#{department}
and userId=#{userId}
AND customerName LIKE CONCAT(CONCAT('%',#{customerName}),'%')
and telephone LIKE CONCAT(CONCAT('%',#{telephone}),'%')
and totalPrice between #{stotalPrice} and #{etotalPrice}
and createDate between #{screateDate} and #{ecreateDate}
and purpose=#{purpose}
and apartment=#{apartment}
and cusource=#{cusource}
and (select ceil(rand()*(select MAX( id ) from cudata))) <=id

select
from cudata where cusource in (3,6)
AND customerName LIKE CONCAT(CONCAT('%',#{customerName}),'%')
and telephone LIKE CONCAT(CONCAT('%',#{telephone}),'%')
and createDate between #{screateDate} and #{ecreateDate}
