mapper.select:
SELECT * FROM cudayplan where userId=#{userId}
AND DATE_FORMAT(createDate, '%Y%m%d') = DATE_FORMAT(#{createDate}, '%Y%m%d')
group by userId

SELECT * FROM cudayplan where userId=#{userId}
AND DATE_FORMAT(createDate, '%Y%m%d') = DATE_FORMAT(#{nexteDate}, '%Y%m%d')
group by userId

SELECT * FROM cuweekplan
where
userId=#{userId}


        and createDate between #{screateDate} and #{ecreateDate}



group by userId

SELECT * FROM cumonthplan where userId=#{userId}
AND DATE_FORMAT(createDate, '%Y%m') = DATE_FORMAT(#{createDate}, '%Y%m')
group by userId

SELECT * FROM cudedayplan where deId=#{deId}
AND DATE_FORMAT(createDate, '%Y%m%d') = DATE_FORMAT(#{createDate}, '%Y%m%d')
group by deId

SELECT * FROM cudedayplan where deId=#{deId}
AND DATE_FORMAT(createDate, '%Y%m%d') = DATE_FORMAT(#{nexteDate}, '%Y%m%d')
group by deId

SELECT * FROM cudeweekplan
where
deId=#{deId}


		and createDate between #{screateDate} and #{ecreateDate}



group by deId

SELECT * FROM cudemonthplan where deId=#{deId}
AND DATE_FORMAT(createDate, '%Y%m') = DATE_FORMAT(#{createDate}, '%Y%m')
group by deId
